import styled from 'styled-components';

export const DocumentItemStyle = styled.div`
  position: relative;
  padding: 15px;
  margin: 18px 0;
  border: 1px solid #ccc;
  border-radius: 8px;
  cursor: pointer;
  transition: ease-in-out 0.2s;

  &:hover {
    background-color: #e0e0e0;
  }

  .document-type {
    position: absolute;
    bottom: 5px;
    right: 10px;
    font-size: 0.8em;
    color: #666;
  }

  .added-date {
    position: absolute;
    top: 5px;
    left: 10px;
    font-size: 0.8em;
    color: #666;
  }

  .document-details {
    position: absolute;
    bottom: 5px;
    right: 10px;
    font-size: 0.8em;
    color: #666;
  }
`;

export const FolderStyle = styled.div<{ isOpen: boolean }>`
  padding: 20px 10px;
  margin: 5px 0;
  border: 1px solid #ccc;
  border-radius: 8px;
  background-color: ${({ isOpen }) => (isOpen ? '#e6e6e6' : '#a89cf7')};
  color: #24286e;
  cursor: pointer;
  font-weight: bold;
  transition: ease-in-out 0.2s;


  &:hover {
    background-color: #514ac2;
    color: #fff;

  }

  .folder-icon {
    position: absolute;
    bottom: 5px;
    left: 10px;
    color: white;
    font-size: 1.2em; // Adjust size as needed
  }
`;

export const NestedDocumentsStyle = styled.div`
  padding-left: 20px;
`;

export const FilterInput = styled.input`
  padding: 8px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 4px;
  width: calc(100% - 20px);
`;
