import React, { useState } from 'react';
import { Document } from '../types/documents';
import DocumentItem from './DocumentItem';
import Folder from './Folder';
import { FilterInput } from './StyledComponents';

interface DocumentsProps {
  documents: Document[];
}

const Documents: React.FC<DocumentsProps> = ({ documents }) => {
  const [filter, setFilter] = useState('');
  const [sortCriterion, setSortCriterion] = useState<'name' | 'size' | 'added'>('name');
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc');

  const sortDocuments = (docs: Document[]): Document[] => {
    return [...docs].sort((a, b) => {
      let comparison = 0;
      if (sortCriterion === 'name') {
        comparison = a.name.localeCompare(b.name);
      } else if (sortCriterion === 'size' && a.size && b.size) {
        comparison = a.size - b.size;
      } else if (sortCriterion === 'added') {
        comparison = new Date(a.added).getTime() - new Date(b.added).getTime();
      }

      return sortDirection === 'asc' ? comparison : -comparison;
    });
  };

  const filteredSortedDocuments = sortDocuments(documents.filter(doc =>
    doc.name.toLowerCase().includes(filter.toLowerCase())
  ));

  return (
    <div>
      <FilterInput
        type="text"
        placeholder="Filter by filename"
        onChange={(e) => setFilter(e.target.value)}
      />
      <div>
        <button onClick={() => { setSortCriterion('name'); setSortDirection('asc'); }}>Sort by Name ↑</button>
        <button onClick={() => { setSortCriterion('name'); setSortDirection('desc'); }}>Sort by Name ↓</button>
        <button onClick={() => { setSortCriterion('size'); setSortDirection('asc'); }}>Sort by Size ↑</button>
        <button onClick={() => { setSortCriterion('size'); setSortDirection('desc'); }}>Sort by Size ↓</button>
        <button onClick={() => { setSortCriterion('added'); setSortDirection('asc'); }}>Sort by Date ↑</button>
        <button onClick={() => { setSortCriterion('added'); setSortDirection('desc'); }}>Sort by Date ↓</button>
      </div>
      {filteredSortedDocuments.map((doc, index) =>
        doc.type === 'folder' ? (
          <Folder key={index} folder={doc} />
        ) : (
          <DocumentItem key={index} document={doc} />
        )
      )}
    </div>
  );
};

export default Documents;
