import styled from 'styled-components';

export const DocumentItemStyle = styled.div`
  position: relative;
  padding: 15px;
  margin: 10px auto;
  max-width: 400px;
  border: 1px solid #ccc;
  border-radius: 18px;
  cursor: pointer;
  transition: ease-in-out 0.2s;

  &:hover {
    background-color: #e0e0e0;
  }

  .document-type {
    position: absolute;
    bottom: 5px;
    right: 10px;
    font-size: 0.8em;
    color: #666;
  }

  .added-date {
    position: absolute;
    top: 5px;
    left: 10px;
    font-size: 0.8em;
    color: #666;
  }

  .document-details {
    position: absolute;
    bottom: 5px;
    right: 10px;
    font-size: 0.8em;
    color: #666;
  }
`;

export const FolderStyle = styled.div<{ isOpen: boolean }>`
  padding: 20px 10px;
  margin: 5px 0;

  border: ${({ isOpen }) => (isOpen ? '3px solid #2c234a' : '3px solid #a89cf7')};
  border-radius: 8px;
  background-color: ${({ isOpen }) => (isOpen ? '#a89cf7' : '#a89cf7')};
  color: #24286e;
  cursor: pointer;
  font-weight: bold;
  text-align: left;
  transition: ease-in-out 0.2s;


  &:hover {
    background-color: #514ac2;
    color: #fff;
    border: 3px solid #a89cf7;

  }

  .folder-icon {
    position: absolute;
    bottom: 5px;
    left: 10px;
    color: white;
    font-size: 1.2em; // Adjust size as needed
  }
`;

export const NestedDocumentsStyle = styled.div`
  padding: 10px 0;
  background: slategrey;
  border-radius: 8px;
  margin: 0 20px;
`;

export const FilterInput = styled.input`
  padding: 8px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 4px;
  width: calc(100% - 20px);
`;

export const SortButton = styled.button`
  padding: 8px 12px;
  margin-right: 8px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #0056b3;
  }

  &:focus {
    outline: none;
  }

  &:last-child {
    margin-right: 0;
  }
`;
