import React, { useState } from 'react';
import { Document } from '../types/documents'; // Adjust the import path as necessary
import DocumentItem from './DocumentItem'; // Adjust the import path as necessary

interface FolderProps {
  folder: Document;
}

const Folder: React.FC<FolderProps> = ({ folder }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div>
      <div onClick={() => setIsOpen(!isOpen)} style={{ cursor: 'pointer', fontWeight: 'bold' }}>
        {folder.name}
      </div>
      {isOpen && folder.files && (
        <div style={{ paddingLeft: '20px' }}>
          {folder.files.map((file, index) => (
            <DocumentItem key={index} document={file} />
          ))}
        </div>
      )}
    </div>
  );
};

export default Folder;
