// src/components/Documents.tsx

import React, { useState } from 'react';
import { Document } from '../types/documents';

interface Props {
  documents: Document[];
}

const Documents: React.FC<Props> = ({ documents }) => {
  const [filter, setFilter] = useState('');
  const [sortKey, setSortKey] = useState<'name' | 'added'>('name');

  const sortDocuments = (docs: Document[]) => {
    return docs.sort((a, b) => {
      if (sortKey === 'name') {
        return a.name.localeCompare(b.name);
      } else { // sort by added date
        return new Date(a.added || 0).getTime() - new Date(b.added || 0).getTime();
      }
    });
  };

  const filteredDocuments = documents
    .filter(doc => doc.name.toLowerCase().includes(filter.toLowerCase()))
    .filter(doc => !doc.type || doc.type !== 'folder' || doc.files); // Filter out non-folder documents without files

  const renderDocuments = (docs: Document[]) => {
    return sortDocuments(docs).map((doc, index) => (
      <div key={index}>
        {doc.type === 'folder' ? (
          <Folder folder={doc} />
        ) : (
          <div>{doc.name}</div>
        )}
      </div>
    ));
  };

  const Folder: React.FC<{ folder: Document }> = ({ folder }) => {
    const [isOpen, setIsOpen] = useState(false);

    return (
      <div>
        <div onClick={() => setIsOpen(!isOpen)} style={{ cursor: 'pointer', fontWeight: 'bold' }}>
          {folder.name}
        </div>
        {isOpen && folder.files && (
          <div style={{ paddingLeft: '20px' }}>
            {renderDocuments(folder.files)}
          </div>
        )}
      </div>
    );
  };

  return (
    <div>
      <input 
        type="text" 
        placeholder="Filter by filename" 
        onChange={(e) => setFilter(e.target.value)}
      />
      <div>
        <button onClick={() => setSortKey('name')}>Sort by Name</button>
        <button onClick={() => setSortKey('added')}>Sort by Date</button>
      </div>
      {renderDocuments(filteredDocuments)}
    </div>
  );
};

export default Documents;
