import styled from 'styled-components';

export const DocumentItemStyle = styled.div`
  position: relative;
  padding: 30px 10px;
  margin: 5px 0;
  border: 1px solid #ccc;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #f0f0f0;
  }

  .document-type {
    position: absolute;
    bottom: 5px;
    right: 10px;
    font-size: 0.8em;
    color: #666;
  }

  .added-date {
    position: absolute;
    top: 5px;
    left: 10px;
    font-size: 0.8em;
    color: #666;
  }
`;

export const FolderStyle = styled.div<{ isOpen: boolean, isOpened: boolean }>`
  padding: 10px;
  margin: 5px 0;
  border: 1px solid #ccc;
  border-radius: 4px;
  background-color: ${({ isOpen }) => (isOpen ? '#e6e6e6' : '#3f2e9e')};
  color: #fffff;
  cursor: pointer;
  font-weight: bold;
  &:hover {
    background-color: #e2e2e2;
  }
`;

export const NestedDocumentsStyle = styled.div`
  padding-left: 20px;
`;

export const FilterInput = styled.input`
  padding: 8px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 4px;
  width: calc(100% - 20px);
`;
