import React, { useState } from 'react';
import { Document } from '../types/documents';
import DocumentItem from './DocumentItem';
import { FolderStyle, NestedDocumentsStyle } from './StyledComponents';
// Import the correct icon
import { FaRegFolder } from 'react-icons/fa6'; // Or { FaFolder } for the solid icon

interface FolderProps {
  folder: Document;
}

const Folder: React.FC<FolderProps> = ({ folder }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div>
      <FolderStyle isOpen={isOpen} onClick={() => setIsOpen(!isOpen)}>
        {folder.name}
        {/* Use the icon and style it */}
        <FaRegFolder className="folder-icon" style={{ color: 'white', position: 'absolute', bottom: '5px', left: '10px' }} />
      </FolderStyle>
      {isOpen && folder.files && (
        <NestedDocumentsStyle>
          {folder.files.map((file, index) => (
            <DocumentItem key={index} document={file} />
          ))}
        </NestedDocumentsStyle>
      )}
    </div>
  );
};

export default Folder;
