import React, { useState } from 'react';
import { Document } from '../types/documents';
import DocumentItem from './DocumentItem';
import Folder from './Folder';
import { FilterInput } from './StyledComponents';

interface DocumentsProps {
  documents: Document[];
}

const Documents: React.FC<DocumentsProps> = ({ documents }) => {
  const [filter, setFilter] = useState('');
  const [sortCriterion, setSortCriterion] = useState<'name' | 'size' | 'added'>('name');
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc');

  const toggleSort = (criterion: 'name' | 'size' | 'added') => {
    if (sortCriterion === criterion) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortCriterion(criterion);
      setSortDirection('asc'); // Default to ascending when changing criterion
    }
  };

  // The rest of the component remains the same
  // Ensure the sortDocuments function and rendering logic are correctly implemented as before

  return (
    <div>
      <FilterInput
        type="text"
        placeholder="Filter by filename"
        onChange={(e) => setFilter(e.target.value)}
      />
      <div>
        <button onClick={() => toggleSort('name')}>
          Sort by Name {sortCriterion === 'name' ? (sortDirection === 'asc' ? '↑' : '↓') : ''}
        </button>
        <button onClick={() => toggleSort('size')}>
          Sort by Size {sortCriterion === 'size' ? (sortDirection === 'asc' ? '↑' : '↓') : ''}
        </button>
        <button onClick={() => toggleSort('added')}>
          Sort by Date {sortCriterion === 'added' ? (sortDirection === 'asc' ? '↑' : '↓') : ''}
        </button>
      </div>
      {/* Render sorted and filtered documents */}
    </div>
  );
};

export default Documents;
